//create the TVP data table parameter value
var table = {
    "columns":[
        {"name":"a","type":"VarChar(50)"},
        {"name":"b","type":"Int"}
    ],
    "rows": msg.payload //get rows from payload of prev node
}

//set query mode to execute a stored procedure
msg.queryMode = "execute";

//set the query in payload 
msg.query = "MyCustomStoredProcedure";//set the query to name of the stored procedure to use

//setup the params array 
msg.queryParams = [];

//add param
msg.queryParams.push({
    "name": "tvp",
    "type": "TVP", //can also use "TVP(TestType)"
    "value": table,
    "output": false
});

return msg;

